/**
 * Specifies the shape of the Button.
 *
 * The possible values are:
 * * `pill`
 * * `circle`
 * * `rounded`
 * * `rectangle`
 * * `square`
 *
 */
export declare type ButtonShape = 'pill' | 'circle' | 'rounded' | 'rectangle' | 'square';
